mapper.select:

SELECT * FROM t_answer
	WHERE ans_po_num = #{ans_po_num}
	AND ans_num = #{ans_num}
	AND ans_deleteYn = 'N'

mapper.update:

UPDATE t_answer SET 
	ans_deleteYn = 'Y'
	WHERE ans_num = #{ans_num};


UPDATE t_answer SET 
	ans_deleteYn = 'Y'
	WHERE ans_group_num = #{ans_num};


UPDATE t_answer SET 
	ans_group_num = #{ans_num}
	WHERE ans_num = #{ans_num};
