mapper.insert:
 INSERT INTO T_MEMBER(   ME_ID
					       , ME_PASS 
					       , ME_NICKNAME
					       , ME_DEVLANG  	)
               VALUES(    #{me_id}
              		   , #{me_pass}
              		   , #{me_nickName}
              		   , #{me_devLang}  )

mapper.select:
SELECT COUNT(*) 
FROM T_MEMBER
WHERE ME_ID = #{me_id}

